const elements=document.querySelectorAll("[sscroll]"),scrollObserver=new IntersectionObserver(t=>{t.forEach(t=>{scrollObserverCallback(t,t.isIntersecting)})});elements.forEach(t=>scrollObserver.observe(t));const scrollObserverCallback=(t,e)=>{const s=new Animation(t);e?s.fadeIn():s.fadeOut()};class Animation{constructor(t){this.element=t.target,this.getAttributes()}getAttributes(){this.duration=parseInt(this.element.getAttribute("sscroll-duration"))||600,this.easing=this.element.getAttribute("sscroll-easing")||"ease-in-out",this.iteration=1}fadeIn(){this.element.animate([{opacity:0},{opacity:1}],{duration:this.duration,easing:this.easing})}fadeOut(){this.element.animate([{opacity:1},{opacity:0}],{duration:this.duration,easing:this.easing})}}